

class Tank {
    
    constructor(id, x, y) {
        this.username = '';
        this.id = id;
        this.left = 0;
        this.right = 0;
        this.currentShootCD = 0;
        this.shootCooldown = 10;
        this.canShoot = true;

        this.y = y;
        this.x = x;
    }

    move() {
        if (this.x > 50) {
        this.x += this.left * -3;
        }
        if (this.x < Tank.max_width - 50) {
        this.x += this.right * 3;
        }
    }
}

Tank.width = 70;
Tank.height =  35;
Tank.max_width = 1000;

module.exports = Tank


